import React, { useContext, useState } from "react";
import MovieSearchContext from "../../../MovieSearchContext/MovieSearchContext";
import { IMAGE_BASE_URL } from "../../API";
import './resultList.css';

function ResultList() {
      const {resultSearch} = useContext(MovieSearchContext);
      const [selectItem, setSelectItem] = useState(null);

      const handleSelectItem = (item)=> {
            setSelectItem(item)
      }
      return (
             <div className="resultList_container">
                  <h4 className="mb-5">Search Result</h4>
                  <div className="SearchMovieList_grid">
                        {
                              resultSearch.map((item, index) => (
                                    <div key={index} className="SearchMovie_item">
                                          <img 
                                                onClick={handleSelectItem(item)}
                                                src={item.poster_patth ? `${IMAGE_BASE_URL}${item.poster_patth}`
                                                :`${IMAGE_BASE_URL}${item.backdrop_path}`}
                                                alt={item.title || item.name}
                                                className="searchMovieList_item"
                                                onError={(e) => {
                                                      e.target.onerror = null; // Prevent infinite loop in case fallback image also fails
                                                      e.target.src = 'https://via.placeholder.com/150'; // Fallback image URL
                                                  }}
                                          />
                                          <p className="SearchMovieItem_title">{item.title || item.name}</p>
                                    </div>
                              ))
                        }
                  </div>
            
         

      </div>
      )
}

export default ResultList;
