import React, {useState, useEffect} from "react";
import './MovieDetail.css';
import { API_KEY } from "../../../API";

function MovieDetail(item) {

      const [trailerUrl, setTrailerUrl] = useState('');
      console.log('tri',trailerUrl)
      
      // use useEffect to get api for trailer movie
      useEffect(()=> {
            async function fetchTrailer() {
                  try {
                        const response = await fetch(`https://api.themoviedb.org/3/movie/${item.movie.id}/videos?api_key=${API_KEY}`);
                        const data = await response.json();
                        // find the trailer movie that was clicked
                        const trailerMovie = data.results.find(movie => (movie.site === 'YouTube' && movie.type === 'Trailer'));
                        console.log('data for detail trailer', trailerMovie)
                        if (trailerMovie) 
                              setTrailerUrl(`https://www.youtube.com/embed/${trailerMovie.key}`);
                  }
                  catch (error) {
                        console.error('Error fetching trailer', error);
                  }
            }
            fetchTrailer();

      }, [item.movie.id])

      console.log('selectmovie', item);
      return (    
            <div className="MovieDetail_grid_container">
                  <button className="MovieDetail_close" >X</button>
                  <div className="MovieDetail_item_infor">
                        <h3 className="MovieDetail_item_infor-title">
                              {item.movie.title ? item.movie.title : item.movie.name}
                        </h3>
                        <p className="m-0 MovieDetail_item_infor-relaseDate">
                            Release Date:   {item.movie.release_date ? item.movie.release_date : item.movie.first_air_date}
                        </p>
                        <p className="m-0 MovieDetail_item_infor-vote">
                              Vote: {item.movie.vote_average}
                        </p>
                        <p className="MovieDetail_item_infor-vote--count">
                              Vote count: {item.movie.vote_count}
                        </p>
                        <p>{item.movie.overview ? item.movie.overview : ""}</p>

                  </div>
                  {trailerUrl && (
                        <div className="MovieDetail-trailer">
                              <iframe 
                              width='100%'
                            height="400"
                              src= {trailerUrl ? trailerUrl :  item.backdrop_path}
                              frameborder="0"
                              allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                              allowFullScreen
                              title="Movie Trailer"
                              ></iframe>

                        </div>
                  )

                  }

            </div>
      )
}

export default MovieDetail;
