import React, {useState, useEffect} from 'react';
import './BannerMovie.css';
import MovieList from '../MovieList/MovieList';

function BannerMovie() {

      const API_KEY = '3152693dc53afc973e326322c9f36951';
      const BASE_URL = 'https://api.themoviedb.org/3'; // Base URL for the API
      const IMAGE_BASE_URL = 'https://image.tmdb.org/t/p/original'; // Base URL for the image paths

      
	 const request  = {
		fetchTrending: `/trending/all/week?api_key=${API_KEY}&language=en-US`,
		fetchNetflixOriginals: `/discover/tv?api_key=${API_KEY}&with_network=123`,
		fetchTopRated: `/movie/top_rated?api_key=${API_KEY}&language=en-US`,
		fetchActionMovies: `/discover/movie?api_key=${API_KEY}&with_genres=28`,
		fetchComedyMovies: `/discover/movie?api_key=${API_KEY}&with_genres=35`,
		fetchHorrorMovies: `/discover/movie?api_key=${API_KEY}&with_genres=27`,
		fetchRomanceMovies: `/discover/movie?api_key=${API_KEY}&with_genres=10749`,
		fetchDocumentaries: `/discover/movie?api_key=${API_KEY}&with_genres=99`,
		fetchSearch: `/search/movie?api_key=${API_KEY}&language=en-US`,
	};

      const [movies, setMovies] = useState([]);
      const [randomeMovie, setRandomeMovie] = useState(null);
      console.log('radomemovie', randomeMovie)

      console.log('movies',movies)
      useEffect(()=> {
            async function fetchData(endpoint) {
                  try{
                        const response = await fetch(`${BASE_URL}${endpoint}`);
                        const data = await response.json();
                        console.log('Fetched data:', data);
                        setMovies(data.results);
                        // random a movie to show up for Banner
                        const randomIndex = Math.floor(Math.random() * data.results.length)
                        console.log('size', data.results.length)
                        setRandomeMovie(data.results[randomIndex]);
                  }
                  catch (error) {
                        console.error('Error fetching data', error);
                  }
            }
            fetchData(request.fetchComedyMovies);
      }, []);

      console.log('random',randomeMovie)
      return(
            <>
                  {randomeMovie ? 
                  <>
                        <div className='BannerMovie_container'>
                        
                              <div className='BannerMovie_title'>
                                    {randomeMovie.title} 
                              </div>
                              <img src={`${IMAGE_BASE_URL}${randomeMovie.backdrop_path}`} alt="" />
                              <div className='BannerMovie_buttons'>
                                    <button>
                                          Play
                                    </button>
                                    <button>
                                          Mylist
                                    </button>
                              </div>
                              <p className='bannerMovie_overview'>{randomeMovie.overview}</p>
                        </div>
                        <MovieList />
                  </>
                  : ''
                  }
            </>
      )
}
export default BannerMovie;
