import React, {useState} from "react";
import './MovieList.css';
import MovieDetail from "./MovieDetail/MovieDetail";
import movieTrailer from "movie-trailer";

function MovieList({title, items}) {
      const IMAGE_BASE_URL = 'https://image.tmdb.org/t/p/original'; // Base URL for the image paths
      const [visibleMovies, setVisibleMovies] = useState(10);     //just show up 10 movies
      const [selectMovie, setSelectMovie] = useState(null);       //select the movie by click a corresonding picture

      console.log('movieslist' , items)

      const btnSeeMoreHandle = ()=> {
            // will get more 10 movies after click the button.
            setVisibleMovies(preVisibleMovies => preVisibleMovies + 10);
      }

      // handle when user click a movie's picture
      const clickImgMovieDetailHandle = (movie)=> {
            setSelectMovie(movie);
      }

      // return for MovieList
      return (
            <div className="MovieList_container">
                  <h3 className="mt-4">{title}</h3>
                  <div className="MovieList_grid">
                        {items.slice(0, visibleMovies).map((item, index)=> 
                        <div key={index}                              onClick={clickImgMovieDetailHandle(item)}>
                              <img  
                              src={title === 'Originals' ? `${IMAGE_BASE_URL}${item.poster_path}` : `${IMAGE_BASE_URL}${item.backdrop_path }`} 
                              alt={item.title} 
                              className="MovieList_item"
                             onError={(e) => {
                                 e.target.onerror = null; // Prevent infinite loop in case fallback image also fails
                                 e.target.src = 'https://via.placeholder.com/150'; // Fallback image URL
                             }}
                              /> 
                              <p className="MovieItem-title">{item.title ? item.title : item.name}</p>
                              {console.log('item which didnot display:', item)} {/* Log each item to check its structure */}

                        </div>
                        )}
                  </div>
                  {visibleMovies < items.length &&
                  <button onClick={btnSeeMoreHandle}>See more...</button>
                  }
                  <MovieDetail movie= {movieTrailer}/>
            </div>
      )
}
export default MovieList;
