import React, {useCallback, useContext} from "react";
import MovieSearchContext from "../../../MovieSearchContext/MovieSearchContext";
import { IMAGE_BASE_URL } from "../../API";
import './resultList.css';

function ResultList() {
      const {resultSearch} = useContext(MovieSearchContext);
      return (
             <div className="resultList_container">
                   <h4 className="ml-5">Search Result</h4>
            
            
            {resultSearch.map((result) => {
                  return (
             <div key = {result.title} className="MovieList_container">
            <h3 className="mt-3">{result.title}</h3>
            <div className="MovieList_grid">
            {/* create list of movies */}
            {result.items.map((item, index)=>( 
                  
                  <div key={index}  onClick={() => clickImgMovieDetailHandle(item)}>
                        <img  
                        src={item.title === 'Originals' ? `${IMAGE_BASE_URL}${item.poster_path}` : `${IMAGE_BASE_URL}${item.backdrop_path }`} 
                        alt={item.title} 
                        className="MovieList_item"
                       onError={(e) => {
                           e.target.onerror = null; // Prevent infinite loop in case fallback image also fails
                           e.target.src = 'https://via.placeholder.com/150'; // Fallback image URL
                       }}
                        /> 
                        <p className="MovieItem-title">{item.title ? item.title : item.name}</p>                  
                  </div>)
                  )
            }
            </div>
      </div>              
            )
            })}

      </div>
      )
}

export default ResultList;
