                  catch (error) {
                        console.error('Error fetching data', error);
                  }
            }
            async function initiallizeData(){
                  const Trending = await fetchData(request.fetchTrending);
                  const Originals = await fetchData(request.fetchNetflixOriginals);
                  const TopRated = await fetchData(request.fetchTopRated);
                  const ActionMovies = await fetchData(request.fetchActionMovies);
                  const HorrorMovies = await fetchData(request.fetchHorrorMovies);
                  const RomanceMovies = await fetchData(request.fetchRomanceMovies);
                  const Documentaries = await fetchData(request.fetchDocumentaries);
                  setMovieList({Trending, Originals, TopRated, ActionMovies, HorrorMovies, RomanceMovies, Documentaries});
            }

            initiallizeData();
      }, []);


      console.log('random',randomeMovie)
      return(
            <>
                  {randomeMovie ? 
                  <>
                        <div className='BannerMovie_container'>
                        
                              <div className='BannerMovie_title'>
                                    {randomeMovie.title} 
                              </div>
                              <img src={`${IMAGE_BASE_URL}${randomeMovie.backdrop_path}`} alt="" />
                              <div className='BannerMovie_buttons'>
                                    <button>
                                          Play
                                    </button>
                                    <button>
                                          Mylist
                                    </button>
                              </div>
                              <p className='bannerMovie_overview'>{randomeMovie.overview}</p>
                        </div>
                        {movieList &&
                              <div>
                                    <MovieList title = 'Originals' items= {movieList.Originals}/>
                                    <MovieList title = 'Trending' items= {movieList.Trending}/>
                                    <MovieList title = 'TopRated' items= {movieList.TopRated}/>
                                    <MovieList title = 'ActionMoives' items= {movieList.ActionMovies}/>
                                    <MovieList title = 'HorroMovies' items= {movieList.HorrorMovies}/>
                                    <MovieList title = 'RomanceMovies' items= {movieList.RomanceMovies}/>
                                    <MovieList title = 'Documentaries' items= {movieList.Documentaries}/>
                              </div>
                        }
                        {/* <movieDetail show={}/> */}
                  </>
                  : ''
                  }
            </>
      )
}
export default BannerMovie;
