import React, {useState} from "react";
import './MovieList.css';
import MovieDetail from "./MovieDetail/MovieDetail";
import MovieCategories from "../../../Categories/MovieCategories";
import { IMAGE_BASE_URL } from "../../API";


function MovieList() {
      const categories = MovieCategories();
      const [selectMovie, setSelectMovie] = useState(null);       //select the movie by click a corresonding picture

      // handle when user click a movie's picture
      const clickImgMovieDetailHandle = (movie)=> {
            setSelectMovie(movie);
      }

      // return for MovieList
      return ( 
            <div>
                  {categories.map((categorie) => {
                        return (
                  <div key = {categorie.title} className="MovieList_container">
                        <h3 className="mt-4">{categorie.title}</h3>
                        <div className="MovieList_grid">
                        <div onClick={() => clickImgMovieDetailHandle(categorie.items)}>
                        <img  
                        src={categorie.title === 'Originals' ? `${IMAGE_BASE_URL}${categorie.items.poster_path}` : `${IMAGE_BASE_URL}${categorie.items.backdrop_path }`} 
                        alt={categorie.items.title} 
                        className="MovieList_item"
                        onError={(e) => {
                        e.target.onerror = null; // Prevent infinite loop in case fallback image also fails
                        e.target.src = 'https://via.placeholder.com/150'; // Fallback image URL
                        }}
                        /> 
                        <p className="MovieItem-title">{categorie.items.title ? categorie.items.title : categorie.items.name}</p>
                        {console.log('item which didnot display:', categorie.items)} {/* Log each item to check its structure */}

                        </div>
                  
                        </div>
                        {selectMovie &&
                              <MovieDetail movie= {selectMovie} />
                        }
                  </div>              
                        )
                  })}
            </div>
      )
}
export default MovieList;
