import React, { useContext } from 'react';
import './BannerMovie.css';
import MovieList from '../MovieList/MovieList';
import MovieContext from '../../../MovieContext/MovieContext';

function BannerMovie() {
      const IMAGE_BASE_URL = 'https://image.tmdb.org/t/p/original'; // Base URL for the image paths
      const {bannerMovie, movieLists} = useContext(MovieContext);     

      console.log('effect movies',bannerMovie)
     

      console.log('random',bannerMovie)
      return(
            <>
                  {bannerMovie ? 
                  <>
                        <div className='BannerMovie_container'>
                        
                              <div className='BannerMovie_title'>
                                    {bannerMovie.title} 
                              </div>
                              <img src={`${IMAGE_BASE_URL}${bannerMovie.backdrop_path}`} alt={bannerMovie.title} />
                              <div className='BannerMovie_buttons'>
                                    <button>
                                          Play
                                    </button>
                                    <button>
                                          Mylist
                                    </button>
                              </div>
                              <p className='bannerMovie_overview'>{BannerMovie.overview}</p>
                        </div>
                        {movieLists &&
                              <div>
                                    {/* <MovieList title = 'Originals' items= {movieLists.Originals}/>
                                    <MovieList title = 'Trending' items= {movieLists.Trending}/>
                                    <MovieList title = 'TopRated' items= {movieLists.TopRated}/>
                                    <MovieList title = 'ActionMoives' items= {movieLists.ActionMovies}/>
                                    <MovieList title = 'HorroMovies' items= {movieLists.HorrorMovies}/>
                                    <MovieList title = 'RomanceMovies' items= {movieLists.RomanceMovies}/>
                                    <MovieList title = 'Documentaries' items= {movieLists.Documentaries}/> */}
                              </div>
                        }
                        {/* <movieDetail show={}/> */}
                  </>
                  : ''
                  }
            </>
      )
}
export default BannerMovie;
