import React, { useState, useCallback } from "react";
import SearchSVG from "../../SearchSVG";
import { fetchData,API_KEY } from "../../API";
import './searchForm.css';

function SearchForm() {
      // useState for input 
      const [inputData, setInputData] = useState('');
      const [resultSearch, setResultSearch] = useState(null);

      // handle for input
      const handleInputChange = useCallback((e) => {
            setInputData(e.target.value);
        }, []);

      //   handle for reset button
      const handleReset = useCallback(() => {
            setInputData('');
            setResultSearch([]);
      })

      // handle for click search button
      const searchClickHandle = useCallback(async(e) => {
            e.preventDefault();
            if(inputData.trim()) {
                  try{
                        const searchData = await fetchData(`/search/multi?api_key=${API_KEY}&include_adult=false&language=en-US&page=1&query=${inputData}`);
                        setResultSearch(searchData);
                        console.log('result search', resultSearch)
                  }
                  catch (error){
                        console.error('Error fetching search results:', error);
                  }
            }
      }
      )

      return (
            <form className="SearchForm">
                  <div className="SearchForm_input">
                        <input 
                        type="text" 
                        value={inputData}
                        placeholder="Search for a movie..."
                        onChange={handleInputChange} />
                        <SearchSVG className='searchIcon'/>
                  </div>
                  <div className="SearchForm_btn">
                        <button className="btnReset" onClick={handleReset}> RESET </button>
                        <button className="btnSearch" onClick={searchClickHandle}> SEARCH </button>
                  </div>
            </form>
      )
}

export default SearchForm;
