            async function fetchTrailer() {
                  
            }

      }, [])

      console.log('selectmovie', item);
      return (    
            <div className="MovieDetail_grid_container">
                  <button className="MovieDetail_close" >X</button>
                  <div className="MovieDetail_item_infor">
                        <h3 className="MovieDetail_item_infor-title">
                              {item.movie.title ? item.movie.title : item.movie.name}
                        </h3>
                        <p className="MovieDetail_item_infor-relaseDate">
                              {item.movie.release_date ? item.movie.release_date : item.movie.first_air_date}
                        </p>
                        <p className="MovieDetail_item_infor-vote">
                              {item.movie.vote_average}
                        </p>
                        <p className="MovieDetail_item_infor-vote--count">
                              {item.movie.vote_count}
                        </p>
                        <p>{item.movie.overview ? item.movie.overview : ""}</p>

                  </div>
                  {trailerUrl && (
                        <div className="MovieDetail-trailer">
                              <iframe 
                              width="560"
                            height="615"
                              src= {trailerUrl}
                              frameborder="0"
                              allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                              allowFullScreen
                              title="Movie Trailer"
                              ></iframe>

                        </div>
                  )

                  }

            </div>
      )
}

export default MovieDetail;
